# collections_ordereddict_iter.py
import collections
print('Dizionario Normale:') d = {} d['a'] = 'A' d['b'] = 'B' d['c'] = 'C'
for k, v in d.items(): print(k, v)
print('\nOrderedDict:') d = collections.OrderedDict() d['a'] = 'A' d['b'] = 'B' d['c'] = 'C'
for k, v in d.items(): print(k, v)
