def my_generator():
    try:
        for i in range(5):
            print 'Trattengo', i
            yield i
    except GeneratorExit:
        print 'Uscita prematura'

g = my_generator()
print g.next()
g.close()

try:
    print 'Premere Invio o Ctrl-C:',
    ignored = raw_input()
