import heapq from heapq_showtree import show_tree from heapq_heapdata import data
heap = [] print 'random :', data print
for n in data: print 'aggiungo %3d:' % n heapq.heappush(heap, n) show_tree(heap)

import heapq from heapq_showtree import show_tree from heapq_heapdata import data
heapq.heapify(data) print 'inizio:' show_tree(data)
for n in [0, 7, 13, 9, 5]: smallest = heapq.heapreplace(data, n) print 'sostituzione di %2d con %2d:' % (smallest, n) show_tree(data)
