import string

for n in dir(string):
    if n.startswith('_'):
        continue
    v = getattr(string, n)
    if isinstance(v, basestring):
        print '%s=%s' % (n, repr(v))
        print

import string

values = { 'var':'foo' }

t = string.Template("$var esiste ma $mancante non è fornito")

try:
    print 'TEMPLATE:', t.substitute(values)
except KeyError, err:
    print 'ERRORE:', str(err)

print 'TEMPLATE:', t.safe_substitute(values)
