# http_server_GET.py
from http.server import BaseHTTPRequestHandler from urllib import parse
class GetHandler(BaseHTTPRequestHandler):
def do_GET(self): parsed_path = parse.urlparse(self.path) message_parts = [ 'VALORI DEL CLIENT:', 'indirizzi client={} ({})'.format( self.client_address, self.address_string()), 'comando={}'.format(self.command), 'percorso={}'.format(self.path), 'percorso reale={}'.format(parsed_path.path), 'query={}'.format(parsed_path.query), 'versione richiesta={}'.format(self.request_version), '', 'VALORI DEL SERVER:', 'versione server={}'.format(self.server_version), 'versione sys={}'.format(self.sys_version), 'versione protocollo={}'.format(self.protocol_version), '', 'INTESTAZIONI RICEVUTE:', ] for name, value in sorted(self.headers.items()): message_parts.append( '{}={}'.format(name, value.rstrip()) ) message_parts.append('') message = '\r\n'.join(message_parts) self.send_response(200) self.send_header('Content-Type', 'text/plain; charset=utf-8') self.end_headers() self.wfile.write(message.encode('utf-8'))
if __name__ == '__main__': from http.server import HTTPServer server = HTTPServer(('localhost', 8080), GetHandler) print('Avvio del server, usare per interrompere') server.serve_forever()

# http_server_threads.py
from http.server import HTTPServer, BaseHTTPRequestHandler from socketserver import ThreadingMixIn import threading
class Handler(BaseHTTPRequestHandler):
def do_GET(self): self.send_response(200) self.send_header('Content-Type', 'text/plain; charset=utf-8') self.end_headers() message = threading.currentThread().getName() self.wfile.write(message.encode('utf-8')) self.wfile.write(b'\n')
class ThreadedHTTPServer(ThreadingMixIn, HTTPServer): """Gestisce le richieste in un thread separato."""
if __name__ == '__main__': server = ThreadedHTTPServer(('localhost', 8080), Handler) print('Avvio del server, usare per interrompere')
server.serve_forever()

# http_server_errors.py

from http.server import BaseHTTPRequestHandler


class ErrorHandler(BaseHTTPRequestHandler):

    def do_GET(self):
        self.send_error(404)


if __name__ == '__main__':
    from http.server import HTTPServer
    server = HTTPServer(('localhost', 8080), ErrorHandler)
    print('Avvio del server, usare  per interrompere')

    server.serve_forever()

# http_server_send_header.py
from http.server import BaseHTTPRequestHandler import time
class GetHandler(BaseHTTPRequestHandler):
def do_GET(self): self.send_response(200) self.send_header( 'Content-Type', 'text/plain; charset=utf-8', ) self.send_header( 'Last-Modified', self.date_time_string(time.time()) ) self.end_headers() self.wfile.write('Response body\n'.encode('utf-8'))
if __name__ == '__main__': from http.server import HTTPServer server = HTTPServer(('localhost', 8080), GetHandler) print('Avvio del server, usare per interrompere')
server.serve_forever()
