# itertools_chain.py
from itertools import *
for i in chain([1, 2, 3], ['a', 'b', 'c']): print(i, end=' ') print()

# itertools_zip.py

for i in zip([1, 2, 3], ['a', 'b', 'c']):
    print(i)

# itertools_map.py


def times_two(x):
    return 2 * x


def multiply(x, y):
    return (x, y, x * y)


print('Doppi:')
for i in map(times_two, range(5)):
    print(i)

print('\nMultipli:')
r1 = range(5)
r2 = range(5, 10)
for i in map(multiply, r1, r2):
    print('{:d} * {:d} = {:d}'.format(*i))

print('\nFine:')
r1 = range(5)
r2 = range(2)
for i in map(multiply, r1, r2):
    print(i)

# itertools_count.py
from itertools import *
for i in zip(count(1), ['a', 'b', 'c']): print(i)

# itertools_cycle.py
from itertools import *
for i in zip(range(7), cycle(['a', 'b', 'c'])): print(i)

# itertools_repeat.py
from itertools import *
for i in repeat('over-and-over', 5): print(i)

# itertools_repeat_zip.py
from itertools import *
for i, s in zip(count(), repeat('over-and-over', 5)): print(i, s)

# itertools_repeat_map.py
from itertools import *
for i in map(lambda x, y: (x, y, x * y), repeat(2), range(5)): print('{:d} * {:d} = {:d}'.format(*i))

# itertools_groupby_seq.py
import functools from itertools import * import operator import pprint
@functools.total_ordering class Point:
def __init__(self, x, y): self.x = x self.y = y
def __repr__(self): return '({}, {})'.format(self.x, self.y)
def __eq__(self, other): return (self.x, self.y) == (other.x, other.y)
def __gt__(self, other): return (self.x, self.y) > (other.x, other.y)
# Crea un insieme di dati di istanze di Point data = list(map(Point, cycle(islice(count(), 3)), islice(count(), 7))) print('Dati:') pprint.pprint(data, width=35) print()
# Cerca di raggruppare i dati non ordinati in baase al valore di X print('Raggruppati, non ordinati:') for k, g in groupby(data, operator.attrgetter('x')): print(k, list(g)) print()
# Ordina i dati data.sort() print('Ordinati:') pprint.pprint(data, width=35) print()
# Raggruppa i dati ordinati in base ai valori di X print('Raggruppati, ordinati:') for k, g in groupby(data, operator.attrgetter('x')): print(k, list(g)) print()

# itertools_combinations.py

from itertools import *


def show(iterable):
    first = None
    for i, item in enumerate(iterable, 1):
        if first != item[0]:
            if first is not None:
                print()
            first = item[0]
        print(''.join(item), end=' ')
    print()

print('Coppie univoche:\n')
show(combinations('abcd', r=2))

# itertools_combinations_with_replacement.py

from itertools import *


def show(iterable):
    first = None
    for i, item in enumerate(iterable, 1):
        if first != item[0]:
            if first is not None:
                print()
            first = item[0]
        print(''.join(item), end=' ')
    print()

print('Coppie univoche:\n')
show(combinations_with_replacement('abcd', r=2))
