# os_scandir.py

import os
import sys

for entry in os.scandir(sys.argv[1]):
    if entry.is_dir():
        typ = 'dir'
    elif entry.is_file():
        typ = 'file'
    elif entry.is_symlink():
        typ = 'link'
    else:
        typ = 'sconosciuto'
    print('{name} {typ}'.format(
        name=entry.name,
        typ=typ,
    ))

# os_process_user_example.py
import os
TEST_GID = 502 TEST_UID = 502
def show_user_info(): print('Utente (reale/effettivo) : {} / {}'.format( os.getuid(), os.geteuid())) print('Gruppo (reale/effettivo) : {} / {}'.format( os.getgid(), os.getegid())) print('Gruppi reali :', os.getgroups())
print('PRIMA DELLA MDOIFICA:') show_user_info() print()
try: os.setegid(TEST_GID) except OSError: print('ERRORE: Non è stato possibile cambiare il gruupo effettivo. ' 'Rieseguire come root.') else: print('MODIFICA GRUPPO:') show_user_info() print()
try: os.seteuid(TEST_UID) except OSError: print('ERRORE: Non è stato possibile cambiare l\'utente effettivo. ' 'Rieseguire come root.') else: print('MODIFICA UTENTE:') show_user_info() print()

# os_fork_example.py import os
pid = os.fork()
if pid: print('Id del processo figlio:', pid) else: print('Sono il figlio')

# os_wait_example.py
import os import sys import time
for i in range(2): print('GENITORE {}: Forking {}'.format(os.getpid(), i)) worker_pid = os.fork() if not worker_pid: print('WORKER {}: In partenza'.format(i)) time.sleep(2 + i) print('WORKER {}: Sta finendo'.format(i)) sys.exit(i)
for i in range(2): print('GENITORE: In attesa di {}'.format(i)) done = os.wait() print('GENITORE: Figlio completato:', done)

# os_strerror.py
import errno import os
for num in [errno.ENOENT, errno.EINTR, errno.EBUSY]: name = errno.errorcode[num] print('[{num:>2}] {name:<6}: {msg}'.format( name=name, num=num, msg=os.strerror(num)))
