# random_random.py
import random
for i in range(5): print('%04.3f' % random.random(), end=' ') print()

# random_uniform.py
import random
for i in range(5): print('{:04.3f}'.format(random.uniform(1, 100)), end=' ') print()

# random_randrange.py
import random
for i in range(3): print(random.randrange(0, 101, 5), end=' ') print()
