# signal_signal.py
import signal import os import time
def receive_signal(signum, stack): print('Ricevuto:', signum)
# Registra i gestori di segnale signal.signal(signal.SIGUSR1, receive_signal) signal.signal(signal.SIGUSR2, receive_signal)
# Stampa l'ID del processo in modo che possa essere usato con 'kill' # per inviare segnali a questo programma print('Il mio PID è:', os.getpid())
while True: print('In attesa...') time.sleep(3)

# signal_getsignal.py

import signal


def alarm_received(n, stack):
    return

signal.signal(signal.SIGALRM, alarm_received)

signals_to_names = {
    getattr(signal, n): n
    for n in dir(signal)
    if n.startswith('SIG') and '_' not in n
}

for s, name in sorted(signals_to_names.items()):
    handler = signal.getsignal(s)
    if handler is signal.SIG_DFL:
        handler = 'SIG_DFL'
    elif handler is signal.SIG_IGN:
        handler = 'SIG_IGN'
    print('{:<10} ({:2d}):'.format(name, s), handler)

# signal_threads.py
import signal import threading import os import time
def signal_handler(num, stack): print('Segnale {} ricevuto in {}'.format( num, threading.currentThread().name))
signal.signal(signal.SIGUSR1, signal_handler)
def wait_for_signal(): print('In attesa dell\'entrata del segnale', threading.currentThread().name) signal.pause() print('Attesa terminata')
# Inizia un thread che non riceverà il segnale receiver = threading.Thread( target=wait_for_signal, name='ricevente', ) receiver.start() time.sleep(0.1)
def send_signal(): print('Invio del segnale', threading.currentThread().name) os.kill(os.getpid(), signal.SIGUSR1)
sender = threading.Thread(target=send_signal, name='mittente') sender.start() sender.join()
# Attende il thread per vedere il segnale (non succederà!) print('In attesa di', receiver.name) signal.alarm(2) receiver.join()
