# http_server_GET.py
from http.server import BaseHTTPRequestHandler from urllib import parse
class GetHandler(BaseHTTPRequestHandler):
def do_GET(self): parsed_path = parse.urlparse(self.path) message_parts = [ 'VALORI DEL CLIENT:', 'indirizzi client={} ({})'.format( self.client_address, self.address_string()), 'comando={}'.format(self.command), 'percorso={}'.format(self.path), 'percorso reale={}'.format(parsed_path.path), 'query={}'.format(parsed_path.query), 'versione richiesta={}'.format(self.request_version), '', 'VALORI DEL SERVER:', 'versione server={}'.format(self.server_version), 'versione sys={}'.format(self.sys_version), 'versione protocollo={}'.format(self.protocol_version), '', 'INTESTAZIONI RICEVUTE:', ] for name, value in sorted(self.headers.items()): message_parts.append( '{}={}'.format(name, value.rstrip()) ) message_parts.append('') message = '\r\n'.join(message_parts) self.send_response(200) self.send_header('Content-Type', 'text/plain; charset=utf-8') self.end_headers() self.wfile.write(message.encode('utf-8'))
if __name__ == '__main__': from http.server import HTTPServer server = HTTPServer(('localhost', 8080), GetHandler) print('Avvio del server, usare <Ctrl-C> per interrompere') server.serve_forever()

# http_server_POST.py
import cgi from http.server import BaseHTTPRequestHandler import io
class PostHandler(BaseHTTPRequestHandler):
def do_POST(self): # Parse the form data posted form = cgi.FieldStorage( fp=self.rfile, headers=self.headers, environ={ 'REQUEST_METHOD': 'POST', 'CONTENT_TYPE': self.headers['Content-Type'], } )
# Inizio della risposta self.send_response(200) self.send_header('Content-Type', 'text/plain; charset=utf-8') self.end_headers()
out = io.TextIOWrapper( self.wfile, encoding='utf-8', line_buffering=False, write_through=True, )
out.write('Client: {}\n'.format(self.client_address)) out.write('User-agent: {}\n'.format( self.headers['user-agent'])) out.write('Percorso: {}\n'.format(self.path)) out.write('Dati del form:\n')
# Ritorna le informazioni di ciò che era stato inviato nel form for field in form.keys(): field_item = form[field] if field_item.filename: # Il campo contiene un file inviato file_data = field_item.file.read() file_len = len(file_data) del file_data out.write( '\tUploaded {} as {!r} ({} bytes)\n'.format( field, field_item.filename, file_len) ) else: # Valori del normali out.write('\t{}={}\n'.format( field, form[field].value))
# Disconnette il wrapper di codifica dal buffer sottostante # in modo che l'eliminazione del wrapper non chiude # il socket, che è ancora in uso dal server. out.detach()
if __name__ == '__main__': from http.server import HTTPServer server = HTTPServer(('localhost', 8080), PostHandler) print('Server in esecuzione, usare <Ctrl-C> per terminare') server.serve_forever()

# urllib_request_upload_files.py
import io import mimetypes from urllib import request import uuid
class MultiPartForm: """Raccoglie i dati da usare quando si invia un form."""
def __init__(self): self.form_fields = [] self.files = [] # Usa una grande string di byte casuali per separere le # parti dei dati MIME. self.boundary = uuid.uuid4().hex.encode('utf-8') return
def get_content_type(self): return 'multipart/form-data; boundary={}'.format( self.boundary.decode('utf-8'))
def add_field(self, name, value): """Aggiunge un campo semplice ai dati del form.""" self.form_fields.append((name, value))
def add_file(self, fieldname, filename, fileHandle, mimetype=None): """Aggiunte un file da inviare.""" body = fileHandle.read() if mimetype is None: mimetype = ( mimetypes.guess_type(filename)[0] or 'application/octet-stream' ) self.files.append((fieldname, filename, mimetype, body)) return
@staticmethod def _form_data(name): return ('Content-Disposition: form-data; ' 'name="{}"\r\n').format(name).encode('utf-8')
@staticmethod def _attached_file(name, filename): return ('Content-Disposition: file; ' 'name="{}"; filename="{}"\r\n').format( name, filename).encode('utf-8')
@staticmethod def _content_type(ct): return 'Content-Type: {}\r\n'.format(ct).encode('utf-8')
def __bytes__(self): """Rttorna una stringa di byte che rappresentano i dati del form, compreso i file allegati """ buffer = io.BytesIO() boundary = b'--' + self.boundary + b'\r\n'
# Aggiunge i campi del form for name, value in self.form_fields: buffer.write(boundary) buffer.write(self._form_data(name)) buffer.write(b'\r\n') buffer.write(value.encode('utf-8')) buffer.write(b'\r\n')
# Aggiunge i fiel da inviare for f_name, filename, f_content_type, body in self.files: buffer.write(boundary) buffer.write(self._attached_file(f_name, filename)) buffer.write(self._content_type(f_content_type)) buffer.write(b'\r\n') buffer.write(body) buffer.write(b'\r\n')
buffer.write(b'--' + self.boundary + b'--\r\n') return buffer.getvalue()
if __name__ == '__main__': # Creat il form con campi semplici form = MultiPartForm() form.add_field('firstname', 'Doug') form.add_field('lastname', 'Hellmann')
# Aggiunge un file fasullo form.add_file( 'biography', 'bio.txt', fileHandle=io.BytesIO(b'Python developer and blogger.'))
# Costruisce la richiesta, compresa la stringa di byte # per i dati da inviare. data = bytes(form) r = request.Request('http://localhost:8080/', data=data) r.add_header( 'User-agent', 'PyMOTW (https://pymotw.com/)', ) r.add_header('Content-type', form.get_content_type()) r.add_header('Content-length', len(data))
print() print('DATI IN USCITA:') for name, value in r.header_items(): print('{}: {}'.format(name, value)) print() print(r.data.decode('utf-8'))
print() print('RISPOSTA DEL SERVER:') print(request.urlopen(r).read().decode('utf-8'))

# urllib_request_nfs_handler.py
import io import mimetypes import os import tempfile from urllib import request from urllib import response
class NFSFile:
def __init__(self, tempdir, filename): self.tempdir = tempdir self.filename = filename with open(os.path.join(tempdir, filename), 'rb') as f: self.buffer = io.BytesIO(f.read())
def read(self, *args): return self.buffer.read(*args)
def readline(self, *args): return self.buffer.readline(*args)
def close(self): print('\nNFSFile:') print(' sto smontando {}'.format( os.path.basename(self.tempdir))) print(' quando {} è chiuso'.format( os.path.basename(self.filename)))
class FauxNFSHandler(request.BaseHandler):
def __init__(self, tempdir): self.tempdir = tempdir super().__init__()
def nfs_open(self, req): url = req.full_url directory_name, file_name = os.path.split(url) server_name = req.host print('Simulazione montaggio FauxNFSHandler:') print(' Percorso remoto : {}'.format(directory_name)) print(' Server : {}'.format(server_name)) print(' Percorso locale : {}'.format( os.path.basename(tempdir))) print(' Nome file : {}'.format(file_name)) local_file = os.path.join(tempdir, file_name) fp = NFSFile(tempdir, file_name) content_type = ( mimetypes.guess_type(file_name)[0] or 'application/octet-stream' ) stats = os.stat(local_file) size = stats.st_size headers = { 'Content-type': content_type, 'Content-length': size, } return response.addinfourl(fp, headers, req.get_full_url())
if __name__ == '__main__': with tempfile.TemporaryDirectory() as tempdir: # Popola il file temporaneo per la simulazione filename = os.path.join(tempdir, 'file.txt') with open(filename, 'w', encoding='utf-8') as f: f.write('Contents of file.txt')
# Costruisce un opener con il nostro handler NFS # e lo registra come predefinito. opener = request.build_opener(FauxNFSHandler(tempdir)) request.install_opener(opener)
# Apre il file tramite un URL. resp = request.urlopen( 'nfs://remote_server/path/to/the/file.txt' ) print() print('CONTENUTO LETTO:', resp.read()) print('URL :', resp.geturl()) print('INTESTAZIONI :') for name, value in sorted(resp.info().items()): print(' {:<15} = {}'.format(name, value)) resp.close()
