# weakref_ref_callback.py import weakref
class ExpensiveObject(object): def __del__(self): print('(Eliminazione di %s)' % self)
def callback(reference): """Chiamato quando l'oggetto referenziato viene eliminato""" print('callback(', reference, ')')
obj = ExpensiveObject() r = weakref.ref(obj, callback)
print('obj:', obj) print('ref:', r) print('r():', r())
print('Eliminazione di obj') del obj print('r():', r())

# weakref_finalize.py

import weakref


class ExpensiveObject:

    def __del__(self):
        print('(In eliminazione {})'.format(self))


def on_finalize(*args):
    print('on_finalize({!r})'.format(args))


obj = ExpensiveObject()
weakref.finalize(obj, on_finalize, 'argumento extra')

del obj

# weakref_finalize_atexit.py
import sys import weakref
class ExpensiveObject:
def __del__(self): print('(In eliminazione {})'.format(self))
def on_finalize(*args): print('on_finalize({!r})'.format(args))
obj = ExpensiveObject() f = weakref.finalize(obj, on_finalize, 'argumento extra') f.atexit = bool(int(sys.argv[1]))

# weakref_finalize_reference.py

import gc
import weakref


class ExpensiveObject:

    def __del__(self):
        print('(In eliminazione {})'.format(self))


def on_finalize(*args):
    print('on_finalize({!r})'.format(args))


obj = ExpensiveObject()
obj_id = id(obj)

f = weakref.finalize(obj, on_finalize, obj)
f.atexit = False

del obj

for o in gc.get_objects():
    if id(o) == obj_id:
        print('trovato oggetto non raccoglibile in gc')

# weakref_finalize_reference_method.py

import gc
import weakref


class ExpensiveObject:

    def __del__(self):
        print('(In eliminazione {})'.format(self))

    def do_finalize(self):
        print('do_finalize')


obj = ExpensiveObject()
obj_id = id(obj)

f = weakref.finalize(obj, obj.do_finalize)
f.atexit = False

del obj

for o in gc.get_objects():
    if id(o) == obj_id:
        print('trovato oggetto non raccoglibile in gc')
